mapper.update:
UPDATE Users
SET username = #{username}
  , password = #{password}
  , role = #{role}
WHERE username = #{username}

MERGE INTO users u USING (
SELECT #{user.username} AS username, #{user.password} AS password, #{user.role} AS role FROM dual
) src ON (u.username = src.username) WHEN MATCHED THEN UPDATE SET u.password = src.password, u.role = src.role WHEN NOT MATCHED THEN INSERT (username, password, role) VALUES (src.username, src.password, src.role)

delete.if:
DELETE FROM users
    WHERE username IN

    #{id}

mapper.select:
SELECT username , password , role FROM Users
username IN
#{item}
